query Prontuario($prontuarioId: Int!) {
  prontuario(id: $prontuarioId) {
    id
    dataAtendimento
    descricao
    tratamento
    exames
    animalId
    animal {
      id
      nome
      especie
      raca
      dataNascimento
      tutorId
      tutor {
        id
        nome
        email
        telefone
      }
    }
  }
}
